#include <stdio.h>
#define QTD_CARACTERES 1024

int main(void){

    char msg[QTD_CARACTERES+1];
    int i;

    fgets(msg, QTD_CARACTERES, stdin);

    for(i=0; i<QTD_CARACTERES;i++){
        char letra = msg[i];
        if(letra == '\0'){
            break;
        }
        if(letra >= 'A' && letra <= 'Z'){
            putchar(letra);
        }
    }
    putchar('\n');
    
    return 0;
}


